% tilings of a 4 board:
\begin{tikzpicture}[scale=1.0]
\foreach \col in {0,2}{
\draw[] (\col,0) rectangle (\col+1, 0) rectangle (\col+2, 1);
}
\draw[thick] (0,0) rectangle (4,1);
\node[left] at (6.5,0.5) {4 sq};
\end{tikzpicture}

